import React, {Component} from 'react';
import './TranfficLight.css';
import classNames from 'classnames';

const RED = 0;
const ORANGE = 1;
const GREEN = 2;

class TranfficLight extends Component{

    constructor() {
        super();
        this.state = {
            'currentColor': GREEN
        }; //Vì ở đây currentColor thay đổi liên tục nên sẽ sử dụng state

        setInterval( () => {
           this.setState({
               'currentColor' : this.getNextColor(this.state.currentColor)
           });
        }, 1000);
    }

    getNextColor(color) {
        switch (color) {
            case RED :
                return ORANGE;
            case ORANGE :
                return GREEN;
            case GREEN :
                return RED;
        }
    }

    render() {
        const { currentColor } = this.state;
        return (
            <div className="TranfficLight">
                <div className={classNames('bulb', 'red', {
                    active: currentColor === RED
                })} />
                <div className={classNames('bulb', 'orange', {
                    active: currentColor === ORANGE
                })} />
                <div className={classNames('bulb', 'green', {
                    active: currentColor === GREEN
                })} />
            </div>
        );
    }
}

export default TranfficLight;
