import React, {Component} from 'react';
import './TranfficLight.css';

const RED = 0;
const ORANGE = 1;
const GREEN = 2;

class TranfficLight extends Component{

    constructor() {
        super();
        this.currentColor = RED;
    }

    getNextColor(color) {
        switch (color) {
            case RED :
                return GREEN;
            case ORANGE :
                return RED;
            case GREEN :
                return ORANGE;
        }
    }

    render() {
        return (
            <div className="TranfficLight">
                <div className="b"></div>
            </div>
        );
    }
}

export default TranfficLight;
