import React, {Component} from 'react';
import './TranfficLight.css';

const RED = 0;
const ORANGE = 1;
const GREEN = 2;

class TranfficLight extends Component{

    constructor() {
        super();
        this.currentColor = RED;

        setInterval( () => {
            this.currentColor = this.getNextColor(this.currentColor);
        });
    }

    getNextColor(color) {
        switch (color) {
            case RED :
                return ORANGE;
            case ORANGE :
                return GREEN;
            case GREEN :
                return RED;
        }
    }

    render() {
        return (
            <div className="TranfficLight">
                <div className="bulb red" />
                <div className="bulb orange" />
                <div className="bulb green" />
            </div>
        );
    }
}

export default TranfficLight;
